import { useState } from "react";

export const useCalc = () => {
    const [number, setNumber] = useState(0);

    const addNumber = () => {
        setNumber(number+1);
    }
    const minusNumber = () => {
        setNumber(number-1);
    }
    const setToZero = () => {
        setNumber(0)
    }

    return [number, addNumber, minusNumber, setToZero]
};
