import axios from "axios";
import {useState} from 'react';
function Excuser() {
    const [excuse, setExcuse] = useState("")
    const [category, setCategory] = useState("")

    const generateExcuse = () => {
        axios.get(`https://excuser-three.vercel.app/v1/excuse/${category}/`).then((res) => {
            setExcuse(res.data[0]?.excuse || "No excuse found");
            console.log(category)
        });
    }
    const getCategory = (event) => {
        setCategory(event.target.value);
    }
    return (

    <>
        <p>Input Category for which you want to find excuse for. These are the list of categories available</p>
        <div>
            <button>Family (id range 1-10)</button>
            <button>Office (id range 101-110)</button>
            <button>Children (id range 201-210)</button>
            <button>College (id range 301-310)</button>
            <button>Party (id range 401-410)</button>
            <button>Funny (id range 501-516)</button>
            <button>Gaming (id range 801-820)</button>
            <button>Unbelievable (id range 601-613)</button>
            <button>Developers (id range 701-715)</button>
        </div>
        <input placeholder="Eg: Family" onChange={getCategory}/>
        <button onClick={generateExcuse}>Generate Excuse</button>
        <h1>{excuse}</h1>
        {console.log(excuse)}
    </>
    );
}
export default Excuser;
