import { useState } from 'react';
import './App.css';

function App() {
  const [count, seCount] = useState(0);
  return (
    <div className="App">
      <button onClick={() => {
        seCount(count+1)
      }}>Inscrease</button>
      <button onClick={()=>{
        seCount(count-1)
      }}>Descrease</button>
      <button onClick={()=>{
        seCount(0)
      }}>Set to 0</button>
      {count}
    </div>
  );
}

export default App;
