import { useState } from 'react';
import './App.css';
import {Task} from "./Task"
function App() {
  const [todoList, setTodoList] = useState([]);
  const [NewTask, setNewTask] = useState("");
  const handleChange = (event) => {
    setNewTask(event.target.value);
  };

  const addTask = () => {
    if (NewTask.trim() === "") return;
    const task = {
      id: todoList.length === 0 ? 1 : todoList[todoList.length - 1].id + 1,
      taskName: NewTask,
      status: false,
    }
    setTodoList([...todoList, task]);
    setNewTask("");
  }

  const deleteTask = (id) => {
    setTodoList(todoList.filter((task)=> task.id !== id));
  }

  const update = (id) => {
    const newList = todoList.map((task) => task.id === id ? {...task, status: true} : task);
    setTodoList(newList)
  }

  const clearTasks = () => {
    setTodoList([])
  }

  const clearCompleted = () => {
    setTodoList(todoList.filter((task) => task.status === false))
  }
  return (
    <div className="App">
    <div className='inputBox'>
      <input value={NewTask} onChange={handleChange}></input>
      <button onClick={addTask}>Add Task</button>
      <button onClick={clearTasks}>Clear list</button>
      <button onClick={clearCompleted}>Clear Completed tasks</button>
    </div>
    <div className='List'>
    {todoList.map((task) => {
      return <Task 
        taskName={task.taskName} 
        id={task.id} 
        status={task.status}
        deleteTask={deleteTask}
        update={update}
        />;
    }) 
    }
    </div> 
    </div>
  );
}

export default App;
