export const Task = (task) => {
    return <div>
        <h1>{task.taskName}</h1>
        <div className="task-buttons">
            <button className="complete" onClick={() => task.update(task.id)}>Complete</button>
            <button className="remove" onClick={()=> task.deleteTask(task.id)}>X</button>
        </div>
        </div>;
    
}
